R = (0,2*sqrt(5/2));
S = (-sqrt(2),0);
T = (sqrt(2),0);
U = (-3/2*sqrt(2),sqrt(5/2));
V = (3/2*sqrt(2),sqrt(5/2));

draw(R--U--S--T--V--cycle);
draw(U--V);

