P = (0,0);
Q = (0.5,0.9);
R = (1,0);
NN = (0.5,0);
M = (Q+R)/2;
draw(rightanglemark(Q,NN,P,2.5));
draw(M--P--Q--R--P);
draw(Q--NN);
label("$P$",P,SW);
